import ZombieFighter from "./components/zombieFighter/zombieFighter";
import Team from "./components/team/team";
import Attributes from "./components/attributes";
import { useState } from "react";

const App = () => {
  const [team , setTeam] =useState([]);
  const [money , setMoney] =useState(250);
  const [zombieFighters , setZombieFighters] 
  =useState([
    {
      name: 'abd alfatahh',
      price: 0.1,
      strength:100000000000000,
      agility: 100000000000000,
      img: 'https://cit.uob.edu.bh/wp-content/uploads/sites/2/2021/05/26-1.jpg',
    },
    {
      name: 'Rick',
      price: 70,
      strength: 9,
      agility: 7,
      img: 'https://imageio.forbes.com/specials-images/imageserve/6191301e1b98366532b9bd0e/0x0.jpg?format=jpg&height=900&width=1600&fit=bounds',
    },
    {
      name: 'daryl',
      price: 65,
      strength: 7,
      agility: 8,
      img: 'data:image/jpeg;base64,',
    },
    {
      name: 'michonne',
      price: 55,
      strength: 7,
      agility: 8,
      img: 'https://i.redd.it/eglmomk4lzeb1.jpg',
    },
    {
      name: 'carol',
      price: 40,
      strength: 6,
      agility: 7,
      img: 'data:image/jpeg;base64,',
    },
    {
      name: 'glenn',
      price: 50,
      strength: 7,
      agility: 7,
      img: 'https://i0.wp.com/www.thewrap.com/wp-content/uploads/2015/10/Walking-Dead-Glenn.jpg?w=618&ssl=1',
    },
    {
      name: 'abraham',
      price: 70,
      strength: 9,
      agility: 6,
      img: 'data:image/jpeg;base64,',
    },
    {
      name: 'shane',
      price: 70,
      strength: 9,
      agility: 8,
      img: 'https://i0.wp.com/www.thewrap.com/wp-content/uploads/2016/04/shane-walsh-walking-dead.jpg?w=618&ssl=1',
    },
    {
      name: 'maggie',
      price: 30,
      strength: 5,
      agility: 7,
      img: 'data:image/jpeg;base64,',
    },
    {
      name: 'rossita',
      price: 17,
      strength: 5,
      agility: 9,
      img: 'data:image/jpeg;base64,',
    },
   
  ]);
  const [totalStrength, setTotalStrength] = useState(0);
  const [totalAgility, setTotalAgility] = useState(0);

const handleAdd=(zombieFighter)=>{
    if(money>=zombieFighter.price){
      const newTeam =[...team,zombieFighter]
      setTeam(newTeam)
      setMoney(money - zombieFighter.price)

      const newList = zombieFighters.filter((fighter) => fighter.name !== zombieFighter.name);
      setZombieFighters(newList);
      calculateTotalAttributes(newTeam);
      
      
    }
    
  };

  const calculateTotalAttributes = (team) => {
    let strength = 0;
    let agility = 0;

    for (let i = 0; i < team.length; i++) {
      strength += team[i].strength;
      agility += team[i].agility;
    }

    setTotalStrength(strength);
    setTotalAgility(agility);
  };

  const handleDelete = (one) => {
    const deletedZombie = team.find((fighter) => fighter.name === one.name);
    if (deletedZombie) {
      const newTeam = team.filter((fighter) => fighter.name !== one.name);
      setTeam(newTeam);
      setMoney(money + one.price);
      calculateTotalAttributes(newTeam);

      const newList =[...zombieFighters,one]
      setZombieFighters(newList)
      

    }
  }

  



  return (
    <>
      <div className="container">
        <div>money:{money}</div>
      </div>
      <div>
        <div>ur team:{team.map(one=><div><Team one={one} key={one.name}></Team><button onClick={()=>handleDelete(one)} >delete</button></div>)}</div>
        <Attributes strength={totalStrength} agility={totalAgility} money ={money}></Attributes>
      </div>
      <h2>fighters</h2>
      <div className="container">{zombieFighters.map(zombieFighter =><div><ZombieFighter zombieFighter ={zombieFighter} key = {zombieFighter.name}></ZombieFighter><button onClick={() => handleAdd(zombieFighter)}>add</button></div>)}</div>
    </>
  );
}

export default App